import publicacionAction from '../../actions/publicaciones';

//Ok
exports.getAllPublicacion = (ctx) => {
    ctx.body = publicacionAction.getPublicaciones()
    return ctx
}


//Ok
exports.getPublicacionByID = (ctx) => {
    const ID = ctx.params.id;
    const response = publicacionAction.getPublicacionByID(ID)
    ctx.body = response
    return ctx
}
//Ok
exports.getOfertaID_PublicacionByID = (ctx) => {
    const ID = ctx.params.idOferta;
    const response = publicacionAction.getOfertaID_PublicacionByID(ID)
    ctx.body = response
    return ctx
}

//Ok
exports.getPublicacionByName = (ctx) => {
    const name = ctx.params.name;
    const response = publicacionAction.getPublicacionByName(name)
    ctx.body = response
    return ctx
}

//Ok
exports.createPublicacion = (ctx) => {
    publicacionAction.addPublicacion(ctx.request.body)
    ctx.body = { message: 'User was created' }
    return ctx
}

//falla
exports.removePublicacion = (ctx) => {
    console.log(ctx.params.bool)
    if(ctx.params.bool == 0){
        ctx.body = { message: 'Oferta eliminada' }
        publicacionAction.removeOferta(ctx.params.id,ctx.params.idOferta)

    }
    else if(ctx.params.bool == 1){
        ctx.body = { message: 'Oferta aceptada y publicacion eliminada' }
        publicacionAction.removePublicacion(ctx.params.id)

    }
        return ctx
}

exports.getOfertas = (ctx) => {

    const ID = ctx.params.id;
    const response = publicacionAction.getOfertas(ID)
    ctx.body = response
    return ctx
}

